// Helper: safely get numeric state
function num(entity_id) {
    const s = states[entity_id];
    if (!s) {
        node.warn(`Missing entity: ${entity_id}`);
        return null;
    }
    const n = parseFloat(s.state);
    return isNaN(n) ? null : n;
}

// Helper: safely get boolean state (on/off)
function bool(entity_id) {
    const s = states[entity_id];
    if (!s) {
        node.warn(`Missing entity: ${entity_id}`);
        return null;
    }
    const v = s.state;
    return v === 'on' || v === true;
}

