        "input_number.kitchen_evening_temp",
        "input_number.kitchen_night_temp",
        "binary_sensor.kitchen_window_contact"
    ]);

    // events: all puts the event in msg.payload
    const ev = msg.payload;  
    if (!ev || ev.event_type !== "state_changed") {
        return null;
    }

    const data = ev.event || {};
    const entity_id = data.entity_id;

    if (!entity_id || !watchedEvents.has(entity_id)) {
        return null; // not an entity we care about
    }

    unified = processCurrentStates();
    return unified;
}

