// Helper to safely read nested paths like "rooms.living.trv_temp"
function get(obj, path) {
    return path.split('.').reduce((o, k) => (o && k in o ? o[k] : undefined), obj);
}

function getTargetTemp(room) {
    //Default value
    let targetT = parseFloat(room.day_temp); 

    //If we are away set the away temp and ignore any day period logic
    if (isAway) {
        targetT = targetTemp;
        return targetT;
    }

    let hour = new Date().getHours();
    let period = 'night';

    if (hour >= room.morning_start && hour < room.day_start) {
        period = 'morning';
    } else if (hour >= room.day_start && hour < room.evening_start) {
        period = 'day';
    } else if (hour >= room.evening_start && hour < room.night_start) {
        period = 'evening';
    } else {
        period = 'night';
    }

    switch(period){
        case "morning":
            targetT = parseFloat(room.morning_temp);
            break;
        case "day":
            targetT = parseFloat(room.day_temp);
            break;
        case "evening":
            targetT = parseFloat(room.evening_temp);
            break;
        case "night":
            targetT = parseFloat(room.night_temp);
            break;
        default:
            targetT = parseFloat(room.day_temp);
            break;
    }
    return targetT;
}

//This is standalone logic as it has a different heat source
function Apply_Balcony_Logic() {
    //If we override the logic we should return the msg obj untouched
    const balconyOverride = msg.payload.rooms.balcony.radiator_override;
    if (balconyOverride) return msg;

    const windowOpened = msg.payload.rooms.balcony.window_opened;
    if (windowOpened) {
        msg.payload.rooms.balcony.heater_needed = false;
        return msg;
    }

    const weekday = ["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];
    const d = new Date();
    let day = weekday[d.getDay()];
    switch (day) {
        case "Sunday":
        case "Saturday":
            return;

    }

    let targetT = parseFloat(msg.payload.rooms.balcony.day_temp);

    if (isAway) {
        targetT = targetTemp;
    }
    else {
        targetT = parseFloat(msg.payload.rooms.balcony.day_temp);

        let hour = new Date().getHours();
        let period = 'night';

        if (hour >= 7 && hour < 9) {
            period = 'morning';
        } else if (hour >= 9 && hour < 17) {
            period = 'day';
        } else if (hour >= 17 && hour < 21) {
            period = 'evening';
        } else {
            period = 'night';
        }


        switch(period){
            case "morning":
                targetT = parseFloat(msg.payload.rooms.balcony.morning_temp);
                break;
            case "day":
                targetT = parseFloat(msg.payload.rooms.balcony.day_temp);
                break;
            case "evening":
                targetT = parseFloat(msg.payload.rooms.balcony.evening_temp);
                break;
            case "night":
                targetT = parseFloat(msg.payload.rooms.balcony.night_temp);
                break;
            default:
                targetT = parseFloat(msg.payload.rooms.balcony.day_temp);
                break;
        }
    }

    msg.payload.rooms.balcony.targetTemp = targetT;

    const deltaTemp = parseFloat(msg.payload.rooms.balcony.delta_temp);
    const currentTemp = parseFloat(msg.payload.rooms.balcony.current_temp);
    const lowerLimit = targetT - deltaTemp;
    const upperLimit = targetT + deltaTemp;

    if (currentTemp <= lowerLimit) {
        // Below lower limit — heating ON
        msg.payload.rooms.balcony.heater_needed = true;
    }
    else if (currentTemp >= upperLimit) {
        // Above upper limit — heating OFF
        msg.payload.rooms.balcony.heater_needed = false;
    }
    else {
        // In between — maintain current state or continue heating if already on
        msg.payload.rooms.balcony.heater_needed = true;
    }
}

//This is standalone logic because it doesn't control the heater. 
