function Apply_Kitchen_Logic() {
    //Kitchen is not taken into account when starting the heater
    let hour = new Date().getHours();
    let period = 'night';

    if (hour >= 6 && hour < 9) {
        period = 'morning';
    } else if (hour >= 9 && hour < 17) {
        period = 'day';
    } else if (hour >= 17 && hour < 21) {
        period = 'evening';
    } else {
        period = 'night';
    }

    switch(period){
        case "morning":
        case "day":
            msg.payload.rooms.kitchen.trv_temp = 35;
            msg.payload.rooms.kitchen.heater_needed = true;
            break;
        case "evening":
        case "night":
            msg.payload.rooms.kitchen.trv_temp = 5;
            msg.payload.rooms.kitchen.heater_needed = false;
            break;
        default:
            msg.payload.rooms.kitchen.trv_temp = 35;
            msg.payload.rooms.kitchen.heater_needed = true;
            break;
    }
}


function Apply_Bbls_Logic() {
    const windowOpened = msg.payload.rooms.bbls.window_opened;
    if (windowOpened) {
        msg.payload.rooms.bbls.heater_needed = false;
        msg.payload.rooms.bbls.trv_temp = 5;
