    }

    msg.payload.rooms.bbls.trv_temp = 5;
    const targetT = getTargetTemp(msg.payload.rooms.bbls);
    msg.payload.rooms.bbls.targetTemp = targetT;


    const deltaTemp = parseFloat(msg.payload.rooms.bbls.delta_temp);
    const currentTemp = parseFloat(msg.payload.rooms.bbls.current_temp);

    const upperLimit = targetT + deltaTemp;
    const lowerLimit = targetT - deltaTemp;
    const lowerLimitSoon = targetT - deltaTemp/2;

    //Check temp and set TRV temp and set the heater_on variable
    if (currentTemp <= lowerLimit) {
        // Below lower limit — heating ON
        msg.payload.rooms.bbls.trv_temp = 35;
        msg.payload.rooms.bbls.heater_needed = true;
    }
    else if (currentTemp >= upperLimit) {
        // Above upper limit — heating OFF
        msg.payload.rooms.bbls.trv_temp = 5;
        msg.payload.rooms.bbls.heater_needed = false;
    }

    //It will start soon so we set the TRV to Open and if another room 
    //requires heat this radiator will heat up as well
    if (currentTemp <= lowerLimitSoon) {
        // Below lower limit — heating ON
        msg.payload.rooms.bbls.trv_temp = 35;
        msg.payload.rooms.bbls.would_start_soon = true;
    }
}

