function Check_If_Heat_Needed() {
    let heat_needed = false;

    //If either condition is true we need to start the heating
    if(msg.payload.rooms.living.heater_needed === true
    || msg.payload.rooms.bedroom.heater_needed === true
    || msg.payload.rooms.bbls.heater_needed === true
    ) {
        heat_needed = true;  
    }

    //Heat required - process rooms that are close to needing heat
    // Technically this is no longer needed as the TRV temp will get set
    // keeping it here just in case I decide to make changes in the future
    if (heat_needed) {
        //Living did not request heat but would request soon
        if (msg.payload.rooms.living.heater_needed === false) {
            if (msg.payload.rooms.living.would_start_soon)
                msg.payload.rooms.living.heater_needed = true;
        }

        //Bedroom did not request heat but would request soon
        if (msg.payload.rooms.bedroom.heater_needed === false) {
            if (msg.payload.rooms.bedroom.would_start_soon)
                msg.payload.rooms.bedroom.heater_needed = true;
        }

        //Bbls did not request heat but would request soon
        if (msg.payload.rooms.bbls.heater_needed === false) {
            if (msg.payload.rooms.bbls.would_start_soon)
                msg.payload.rooms.bbls.heater_needed = true;
        }
    }

    msg.payload.heat_needed = heat_needed;
}

function Check_If_Actions_Needed() {
    // The unified object from your question
    const current = msg.payload;

    // Deep-clone so later nodes don't mutate what we store in context
    const curr = JSON.parse(JSON.stringify(current));

    // First message ever: just store and don't trigger
    const prev = flow.get('unified_prev') || null;
    if (!prev)
    {
        flow.set('unified_prev', curr);
        return;
    }

    let changed = {};

    //watchedEntities - Is declared at the top
    for (const path of watchedEntities) {
        const oldVal = get(prev, path);
        const newVal = get(curr, path);

        // Normal strict comparison
        if (oldVal === newVal) continue;

        // Something actually changed here
        changed[path] = { old: oldVal, new: newVal };
    }

    // Update stored snapshot
    flow.set('unified_prev', curr);

    // If nothing changed in watched fields, stop here
    if (Object.keys(changed).length === 0) {
        msg.payload.changes_detected = false;
    } else {
        msg.payload.changes_detected = true;
    }
}


